    slaGR.addQuery('stage', 'in_progress'); // Chá»‰ láº¥y SLA Ä‘ang cháº¡y
    slaGR.query();

    gs.info('ğŸ” Tá»•ng sá»‘ SLA Ä‘ang cháº¡y: ' + slaGR.getRowCount());

