        gs.info('ğŸ“Œ SLA: ' + slaName + ' | Stage: ' + slaGR.stage);

        // Kiá»ƒm tra náº¿u SLA Ä‘ang cháº¡y nhÆ°ng Priority khÃ´ng khá»›p
        if (!slaName.startsWith(formattedPriority)) {
